import * as React from 'react';
import { DataGrid } from '@mui/x-data-grid';
import DetailComponent from './Detail'
const columns = [
  { field: 'name', headerName: 'Name', width: 500 },
  { field: 'createDate', headerName: 'Created Date', width: 130 },
  { field: 'url', headerName: 'Github Repo Url', width: 200 },
];

// const rows = [
//   { id: 1, lastName: 'Snow', firstName: 'Jon', age: 35 },
//   { id: 2, lastName: 'Lannister', firstName: 'Cersei', age: 42 },
//   { id: 3, lastName: 'Lannister', firstName: 'Jaime', age: 45 },
//   { id: 4, lastName: 'Stark', firstName: 'Arya', age: 16 },
//   { id: 5, lastName: 'Targaryen', firstName: 'Daenerys', age: null },
//   { id: 6, lastName: 'Melisandre', firstName: null, age: 150 },
//   { id: 7, lastName: 'Clifford', firstName: 'Ferrara', age: 44 },
//   { id: 8, lastName: 'Frances', firstName: 'Rossini', age: 36 },
//   { id: 9, lastName: 'Roxie', firstName: 'Harvey', age: 65 },
// ];

export default function ListComponent(data) {
    const [open, setOpen] = React.useState(false)
    const rows = data?.data?.map((d,i)=>Object.assign({
        id:i,
        name:d.name,
        createDate:d.createdAt,
        url:d.url
    }))
    const clicked = (event)=>{
      setOpen(true)
    }
    
  return (
    <>
    <div style={{ height: 400, width: '100%' }}>
      <DataGrid
        rows={rows}
        columns={columns}
        pageSize={5}
        rowsPerPageOptions={[5]}
        onRowClick={clicked}
      />
    </div>
    <button onClick={()=>clicked()}>Open</button>
    <DetailComponent open={open}/>
    </>
  );
}
